mapper.select:
SELECT

FROM operation_logs
WHERE user_id = #{userId}
ORDER BY operation_time DESC
LIMIT #{offset}, #{limit}

SELECT

FROM operation_logs
WHERE operation_type = #{operationType}
ORDER BY operation_time DESC
LIMIT #{offset}, #{limit}

SELECT

FROM operation_logs
WHERE operation_time BETWEEN #{startTime} AND #{endTime}
ORDER BY operation_time DESC
LIMIT #{offset}, #{limit}

SELECT

FROM operation_logs
WHERE user_id = #{userId} AND operation_type = #{operationType}
ORDER BY operation_time DESC
LIMIT #{offset}, #{limit}
